import React from 'react';
import { Button, FlatList, Text, TextInput, View } from 'react-native';

export default function ListTask() {


    const DATA = [
        {
            id: 1,
            task: 'task1',
            done: false
        },
        {
            id: 2,
            task: 'task2',
            done: false
        },
        {
            id: 3,
            task: 'task3',
            done: false
        },
        {
            id: 4,
            task: 'task4',
            done: false
        },
    ]

    const renderItemTodo = ({ item }) => {
        return (
            <View style={{ backgroundColor: '#fff', marginVertical: 10 }}>
                <Text>{item.task}</Text>
            </View>
        );
    }

    return (
        <View style={{ flex: 1 }}>
            <FlatList
                data={DATA}
                renderItem={renderItemTodo}
            />
        </View>
    );
}
